import React,{Component} from 'react'
import ReactTable from 'react-table'
import api from './api'

class samplelist extends Component{
    constructor(props){
        super(props)
        this.state = {
            travel_list:[],
            columns:[],
            isloading:false,
        }
    }
    componentWillMount = async() =>{
        fetch("http://localhost:3001/api/flight")
        .then(response=>response.json())
        .then(json=>{
            console.log(json)
        })
        this.setState({isloading:true})
        

        /*await api.getflight().then(flights=>{
            this.setState({
                travel_list:flights.data.data,
                isloading:false,
            })
        })*/
    }
    render(){
        const{travel_list,isloading} = this.state
        const columns = [
            {
                Header: 'ID',
                accessor: '_id',
                filterable: true,
            },
            {
                Header: 'Source',
                accessor: 'sr',
                filterable: true,
            },
            {
                Header: 'Dest',
                accessor: 'dt',
                filterable: true,
            },
            {
                Header: 'Travel-Time',
                accessor: 'time',
                Cell: props => <span>{props.value.join(' / ')}</span>,
            },
        ]

        let showTable = true
        if (!travel_list.length) {
            showTable = false
        }

        return (
            <div>
                {showTable && (
                    <ReactTable
                        data={travel_list}
                        columns={columns}
                        loading={isloading}
                        defaultPageSize={10}
                        showPageSizeOptions={true}
                        minRows={0}
                    />
                )}
        </div>
        )
    }
}

export default samplelist
