import React from 'react';
import logo from './logo.svg';
import './App.css';
import {DropdownMultiple, Dropdown} from 'reactjs-dropdown-component';
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from "react-datepicker";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      age: null,
      errormessage: '',
      startDate:new Date(),
      location:[
        {
          id:0,
          title:'New York',
          selected:false,
          key:'location'
        },
        {
          id:1,
          title:'San Francisco',
          selected:false,
          key:'location'
        }
      ]
    };
  }
  myChangeHandler = (event) => {

    /* let nam = event.target.name;
    let val = event.target.value;
    let err = '';
    if (nam === "age") {
      if (val !="" && !Number(val)) {
        err = <strong>Your age must be a number</strong>;
      }
    }
    this.setState({errormessage: err});
    this.setState({[nam]: val}); */
    var input, filter, ul, li, a, i;
    input = document.getElementById("ip1");
    filter = input.value.toUpperCase();
    /* div = document.getElementById("ip1");
    a = div.getElementsByTagName("a");
    for (i = 0; i < a.length; i++) {
      txtValue = a[i].textContent || a[i].innerText;
      if (txtValue.toUpperCase().indexOf(filter) > -1) {
        a[i].style.display = "";
      } else {
        a[i].style.display = "none";
      }
  } */
  }
  handleChange = date =>{
    this.setState({
      startDate:date
    });
  }
  toggleSelected = (id,key) =>{
    let temp = JSON.parse(JSON.stringify(this.state[key]))
    temp.forEach(item => item.selected = false);
    temp[id].selected = true;
    this.setState({
      [key]: temp
    })
  }
  render() {

    return (
      <div class = "mystyle">

      <form>
      <h1>Hello {this.state.username} {this.state.age}</h1>
      <p class = "para">FROM:</p>
      <DropdownMultiple titleHelper = "Location" title = "Select Source" list = {this.state.location} toggleItem = {this.toggleSelected}/>
      </form>

      <form>
      <p class ="para">TO:</p>
      <DropdownMultiple titleHelper = "Location" title = "Select Destination" list = {this.state.location} toggleItem = {this.toggleSelected}/>
      </form>

      <div class = "frm">
      <form>
      <h1>Hello {this.state.username} {this.state.age}</h1>
      <p class = "para">DEPART DATE:</p>
      <DatePicker titleHelper = "Location" title = "Select Departure Date" selected = {this.startDate} onChange = {this.handleChange}/>
      </form>

      <form>
      <p class = "para">RETURN DATE:</p>
      <DatePicker titleHelper = "Location" title = "Select Return Date" selected = {this.startDate} onChange = {this.handleChange}/>
      </form>
     </div>
      <div class = "frm1">
      <form>
      <h1>Hello {this.state.username} {this.state.age}</h1>
      <p class = "para">CLASS:</p>
      <DropdownMultiple titleHelper = "Location" title = "Select Class" list = {this.state.location} toggleItem = {this.toggleSelected}/>
      </form>

      <form>
      <p class = "para">PASSENGERS:</p>
      <DropdownMultiple titleHelper = "Location" title = "Select Number of Passengers" list = {this.state.location} toggleItem = {this.toggleSelected}/>

      </form>
      </div>
      </div>
    );
  }
}

//ReactDOM.render(<MyForm />, document.getElementById('root'));
export default App;
