import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      age: null,
      errormessage: '',
      location:[
        {
          id:0,
          title:'New York',
          selected:false,
          key:'location'
        },
        {
          id:1,
          title:'San Francisco',
          selected:false,
          key:'location'
        }
      ]
    };
  }
  myChangeHandler = (event) => {

    /* let nam = event.target.name;
    let val = event.target.value;
    let err = '';
    if (nam === "age") {
      if (val !="" && !Number(val)) {
        err = <strong>Your age must be a number</strong>;
      }
    }
    this.setState({errormessage: err});
    this.setState({[nam]: val}); */
    var input, filter, ul, li, a, i;
    input = document.getElementById("ip1");
    filter = input.value.toUpperCase();
    /* div = document.getElementById("ip1");
    a = div.getElementsByTagName("a");
    for (i = 0; i < a.length; i++) {
      txtValue = a[i].textContent || a[i].innerText;
      if (txtValue.toUpperCase().indexOf(filter) > -1) {
        a[i].style.display = "";
      } else {
        a[i].style.display = "none";
      }
  } */
  }
  toggleSelected = (id,key) =>{
    let temp = JSON.parse(JSON.stringify(this.state[key]))
    temp.forEach(item => item.selected = false);
    temp[id].selected = true;
    this.setState({
      [key]: temp
    })
  }
  render() {

    return (
      <div class= "mystyle">
      <form>
      <h1>Hello {this.state.username} {this.state.age}</h1>
      <p class = "para">FROM:</p>
      <DropDownMultiple titleHelper = "Location" title = "location" list = {this.state.location} toggleItem = {this.toggleSelected}/>
      <div id = "dropdown" class = "dropdown-content">
      <input
        type='text'
        name='username'
        onKeyUp={this.myChangeHandler}
        id = "ip1"


      />
       <a href="#about">About</a>
      <a href="#base">Base</a>
      <a href="#blog">Blog</a>
      <a href="#contact">Contact</a>
      <a href="#custom">Custom</a>
      <a href="#support">Support</a>
      <a href="#tools">Tools</a> 


      </div>
      <p class ="para">TO:</p>

      <input
        type='text'
        name='age'
        onChange={this.myChangeHandler}
        id = "ip2"

      />

      {this.state.errormessage}
      </form>
      <div class = "frm">
      <form>
      <h1>Hello {this.state.username} {this.state.age}</h1>
      <p class = "para">DEPART DATE:</p>
      <input
        type='text'
        name='username'
        onChange={this.myChangeHandler}
        id = "ip1"

      />
      <p class = "para">RETURN DATE:</p>
      <input
        type='text'
        name='age'
        onChange={this.myChangeHandler}
        id = "ip2"

      />

      {this.state.errormessage}
      </form>
      </div>
      <div class = "frm1">
      <form>
      <h1>Hello {this.state.username} {this.state.age}</h1>
      <p class = "para">CLASS:</p>
      <input

        type='text'
        name='username'
        onChange={this.myChangeHandler}
        id = "ip1"

      />
      <p class = "para">PASSENGERS:</p>
      <input
        type='text'
        name='age'
        onChange={this.myChangeHandler}
        id = "ip2"

      />

      {this.state.errormessage}
      </form>
      </div>
      </div>
    );
  }
}

