import React from 'react';
import logo from './logo.svg';
import './App.css';
import {DropdownMultiple, Dropdown} from 'reactjs-dropdown-component';
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from "react-datepicker";
//import { Button } from 'react-bootstrap';
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';
import DeleteIcon from '@material-ui/icons/Delete';
//import {BrowserRouter as Router} from 'react-router-dom';
import { MemoryRouter as Router } from 'react-router';
import { Link as RouterLink } from 'react-router-dom';
import history from './history';
import Routers from './Routers';
import Navigation from './NavBar';


//import routes from './routes';
import about from './about.js';
import {Link} from 'react-router-dom';
//import 'bootstrap/dist/css/bootstrap.min.css';



class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      age: null,
      errormessage: '',
      startDate:new Date(),
      endDate:new Date(),
      location_start:[
        {
          id:0,
          title:'New York',
          selected:false,
          key:'location_start'
        },
        {
          id:1,
          title:'San Francisco',
          selected:false,
          key:'location_start'
        }
      ],
      location_end:[
        {
          id:0,
          title:'New York',
          selected:false,
          key:'location_end'
        },
        {
          id:1,
          title:'San Francisco',
          selected:false,
          key:'location_end'
        }
      ],
      class:[
        {
          id:0,
          title:'Economy',
          selected:false,
          key:'class'
        },
        {
          id:1,
          title:'Business',
          selected:false,
          key:'class'
        }
      ],
      pass:[
        {
          id:0,
          title:'1',
          selected:false,
          key:'pass'
        },
        {
          id:1,
          title:'2',
          selected:false,
          key:'pass'
        },
        {
          id:2,
          title:'3',
          selected:false,
          key:'pass'
        },
        {
          id:3,
          title:'4',
          selected:false,
          key:'pass'
        }
      ]
    };
  }
  myChangeHandler = (event) => {

    /* let nam = event.target.name;
    let val = event.target.value;
    let err = '';
    if (nam === "age") {
      if (val !="" && !Number(val)) {
        err = <strong>Your age must be a number</strong>;
      }
    }
    this.setState({errormessage: err});
    this.setState({[nam]: val}); */
    var input, filter, ul, li, a, i;
    input = document.getElementById("ip1");
    filter = input.value.toUpperCase();
    /* div = document.getElementById("ip1");
    a = div.getElementsByTagName("a");
    for (i = 0; i < a.length; i++) {
      txtValue = a[i].textContent || a[i].innerText;
      if (txtValue.toUpperCase().indexOf(filter) > -1) {
        a[i].style.display = "";
      } else {
        a[i].style.display = "none";
      }
  } */
  }
  handleChangeStart = date =>{
    this.setState({
      startDate:date
    });
    //const value = date.format();
  }
  handleChangeEnd = date =>{
    this.setState({
      endDate:date
    });
    //const value = date.format();
  }

  resetThenSet = (id,key) =>{
    let temp = JSON.parse(JSON.stringify(this.state[key]))
    temp.forEach(item => item.selected = false);
    temp[id].selected = true;
    this.setState({
      [key]: temp

    })

    var val = this.state.location_start.filter(function(item){
      return item.title === temp[id].title
    })
    //temp[id].title =val[0].title
    //console.log(val[0].title)

  }

  render() {

    return (


      <div class = "mystyle">
      <Navigation />
      <Routers />
      <form>
      <h1>Hello {this.state.username} {this.state.age}</h1>
      <p class = "para">FROM:</p>
      <Dropdown titleHelper = "Location" title = "Select Source" list = {this.state.location_start} resetThenSet = {this.resetThenSet}/>


      </form>

      <form>
      <p class ="para">TO:</p>
      <Dropdown titleHelper = "Location" title = "Select Destination" list = {this.state.location_end} resetThenSet = {this.resetThenSet}/>
      </form>

      <div class = "frm">
      <form>
      <h1>Hello {this.state.username} {this.state.age}</h1>
      <p class = "para">DEPART DATE:</p>
      <DatePicker titleHelper = "Location" title = "Select Departure Date" selected = {this.state.startDate} onChange = {this.handleChangeStart}/>
      </form>

      <form>
      <p class = "para">RETURN DATE:</p>
      <DatePicker titleHelper = "Location" title = "Select Return Date" selected = {this.state.endDate} onChange = {this.handleChangeEnd}/>
      </form>
     </div>
      <div class = "frm1">
      <form>
      <h1>Hello {this.state.username} {this.state.age}</h1>
      <p class = "para">CLASS:</p>
      <Dropdown titleHelper = "Location" title = "Select Class" list = {this.state.class} resetThenSet = {this.resetThenSet}/>
      </form>

      <form>
      <p class = "para">PASSENGERS:</p>
      <Dropdown titleHelper = "Location" title = "Select Number of Passengers" list = {this.state.pass} resetThenSet = {this.resetThenSet}/>

      </form>
      </div>
      <div class = "b1">

      <Router>
      <Button onClick={() => history.push('/Products')} variant = "contained" color = "primary" endIcon = {<Icon>send</Icon>} size = "large">SUBMIT</Button>{' '}
      </Router>
      </div>
      <div class = "b2">
      <Button  variant = "contained" color = "danger" endIcon = {<DeleteIcon/>}I size = "large">RESET</Button>{' '}
      </div>


      </div>

    );
  }
}

//ReactDOM.render(<MyForm />, document.getElementById('root'));
export default App;
