import React from 'react';
import logo from './logo.svg';
import './App.css';
import {DropdownMultiple, Dropdown} from 'reactjs-dropdown-component';
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from "react-datepicker";
//import { Button } from 'react-bootstrap';
import Button from '@material-ui/core/Button';
import SendIcon from '@material-ui/icons/Send';
import DeleteIcon from '@material-ui/icons/Delete';
//import {BrowserRouter as Router} from 'react-router-dom';
//import axios from 'axios'
import history from './history';
import axios from 'axios'


//import routes from './routes';
import about from './about.js';
import {Link} from 'react-router-dom';
//import 'bootstrap/dist/css/bootstrap.min.css';

const src = ' '
const dst = ' '


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      age: null,
      errormessage: '',
      source: ' ',
      dest: ' ',
      startDate:new Date(),
      endDate:new Date(),
      cls: ' ',
      pas: ' ',
      location_start:[
        {
          id:0,
          title:'New York',
          selected:false,
          key:'location_start'
        },
        {
          id:1,
          title:'San Francisco',
          selected:false,
          key:'location_start'
        }
      ],
      location_end:[
        {
          id:0,
          title:'New York',
          selected:false,
          key:'location_end'
        },
        {
          id:1,
          title:'San Francisco',
          selected:false,
          key:'location_end'
        }
      ],
      class:[
        {
          id:0,
          title:'Economy',
          selected:false,
          key:'class'
        },
        {
          id:1,
          title:'Business',
          selected:false,
          key:'class'
        }
      ],
      pass:[
        {
          id:0,
          title:'1',
          selected:false,
          key:'pass'
        },
        {
          id:1,
          title:'2',
          selected:false,
          key:'pass'
        },
        {
          id:2,
          title:'3',
          selected:false,
          key:'pass'
        },
        {
          id:3,
          title:'4',
          selected:false,
          key:'pass'
        }
      ]
    };
  }

  myChangeHandler = (event) => {

    /* let nam = event.target.name;
    let val = event.target.value;
    let err = '';
    if (nam === "age") {
      if (val !="" && !Number(val)) {
        err = <strong>Your age must be a number</strong>;
      }
    }
    this.setState({errormessage: err});
    this.setState({[nam]: val}); */
    var input, filter, ul, li, a, i;
    input = document.getElementById("ip1");
    filter = input.value.toUpperCase();
    /* div = document.getElementById("ip1");
    a = div.getElementsByTagName("a");
    for (i = 0; i < a.length; i++) {
      txtValue = a[i].textContent || a[i].innerText;
      if (txtValue.toUpperCase().indexOf(filter) > -1) {
        a[i].style.display = "";
      } else {
        a[i].style.display = "none";
      }
  } */
  }
  handleFormData = ()=>{
    //this.setState({source:s})
    //this.setState({dest:d})
    console.log("Source:"+this.state.source)
    console.log("Dest:"+this.state.dest)
    console.log("Start Date:"+this.state.startDate)
    console.log("End Date:"+this.state.endDate)
    console.log("Class:"+this.state.cls)
    console.log("Passengers:"+this.state.pas)
    //src = this.state.source
    //dst = this.state.dest
    const data = {
      source: this.state.source,
      dest: this.state.dest,
      travel_time:4,
    }

    axios.post("http://localhost:3001/api/flight",data)
    .then(data=>{
      console.log(data)
    })
    .catch(error=>{
      console.log(error.response)
    })
    history.push('/api/flight')
    //console.log("Dest:"+this.state.dest)

  }
  handleChangeStart = date =>{
    this.setState({
      startDate:date
    });
    //const value = date.format();
  }
  handleChangeEnd = date =>{
    this.setState({
      endDate:date
    });
    //const value = date.format();
  }

  resetThenSetStart = (id,key) =>{
    let temp = JSON.parse(JSON.stringify(this.state[key]))
    temp.forEach(item => item.selected = false);
    temp[id].selected = true;
    this.setState({
      [key]: temp

    })

    var val = this.state.location_start.filter(function(item){
      return item.title === temp[id].title
    })
    //temp[id].title =val[0].title
    //console.log(val)
    this.setState({source:val[0].title})


  }
  resetThenSetEnd = (id,key) =>{
    let temp = JSON.parse(JSON.stringify(this.state[key]))
    temp.forEach(item => item.selected = false);
    temp[id].selected = true;
    this.setState({
      [key]: temp

    })

    var val = this.state.location_end.filter(function(item){
      return item.title === temp[id].title
    })
    //temp[id].title =val[0].title
    //console.log(val)
    this.setState({dest:val[0].title})


  }
  resetThenSetClass = (id,key) =>{
    let temp = JSON.parse(JSON.stringify(this.state[key]))
    temp.forEach(item => item.selected = false);
    temp[id].selected = true;
    this.setState({
      [key]: temp

    })

    var val = this.state.class.filter(function(item){
      return item.title === temp[id].title
    })
    //temp[id].title =val[0].title
    //console.log(val)
    this.setState({cls:val[0].title})


  }
  resetThenSetPass = (id,key) =>{
    let temp = JSON.parse(JSON.stringify(this.state[key]))
    temp.forEach(item => item.selected = false);
    temp[id].selected = true;
    this.setState({
      [key]: temp

    })

    var val = this.state.pass.filter(function(item){
      return item.title === temp[id].title
    })
    //temp[id].title =val[0].title
    //console.log(val)
    this.setState({pas:val[0].title})


  }

  render() {

    return (


      <div>
        <div class = "container">
      <h1 class = "t">Singapore Airlines</h1>
      <ul class= "u">
